import React, { useState } from 'react';
import LoginPage from './LoginPage';
import RegisterPage from './RegisterPage';
import 'bootstrap/dist/css/bootstrap.min.css'; // Bootstrap CSS import edilmelidir
import './App.css'; // Kendi stil dosyamızı import edelim

const App = () => {
  const [isLoginPage, setIsLoginPage] = useState(true); // Başlangıçta login sayfası gözükecek

  // Kayıt sayfasına geçiş
  const goToRegister = () => {
    setIsLoginPage(false);
  };

  // Giriş sayfasına geçiş
  const goToLogin = () => {
    setIsLoginPage(true);
  };

  return (
    <div>
      {/* Navbar */}
      <nav className="navbar navbar-expand-lg">
        <div className="container-fluid">
          <a className="navbar-brand" href="#">CRM Sistemi</a>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav">
              <li className="nav-item">
                <a className="nav-link active" href="#">Ana Sayfa</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="#">Hakkımızda</a>
              </li>
            </ul>
          </div>
        </div>
      </nav>

      {/* Giriş ya da Kayıt Sayfası */}
      {isLoginPage ? (
        <LoginPage goToRegister={goToRegister} />
      ) : (
        <RegisterPage goToLogin={goToLogin} />
      )}
    </div>
  );
}

export default App;
