import React, { useState } from 'react';
import Modal from 'react-modal';
import './App.css';
import RegisterPage from './RegisterPage'; // Kayıt Sayfası
import LoginPage from './LoginPage'; // Giriş Sayfası
import HomePage from './HomePage'; // Ana Sayfa
import About from './About'; // Hakkımızda Sayfası
import Contact from './Contact'; // İletişim Sayfası

// Modal'ı ekranın ortasında gösterelim
Modal.setAppElement('#root'); // Erişilebilirlik için

const App = () => {
  const [currentPage, setCurrentPage] = useState('home');
  const [isLoginOpen, setIsLoginOpen] = useState(false);
  const [isRegisterOpen, setIsRegisterOpen] = useState(false);

  const openLoginModal = () => setIsLoginOpen(true);
  const closeLoginModal = () => setIsLoginOpen(false);

  const openRegisterModal = () => setIsRegisterOpen(true);
  const closeRegisterModal = () => setIsRegisterOpen(false);

  return (
    <div className="page-container">
      {/* Navbar bölümü */}
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
        <a className="navbar-brand mx-auto" href="#home">CRM Sistemi</a>
        <div className="collapse navbar-collapse">
          <ul className="navbar-nav ml-auto mx-auto">
            <li className="nav-item">
              <Link className="nav-link" to="home" smooth={true} duration={500}>Ana Sayfa</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="about" smooth={true} duration={500}>Hakkımızda</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="contact" smooth={true} duration={500}>İletişim</Link>
            </li>
            <li className="nav-item">
              <a className="nav-link" href="#" onClick={openLoginModal}>Giriş Yap</a>
            </li>
            <li className="nav-item">
              <a className="nav-link" href="#" onClick={openRegisterModal}>Kayıt Ol</a>
            </li>
          </ul>
        </div>
      </nav>

      {/* Sayfa İçeriği */}
      <div className="content">
        {currentPage === 'home' && <HomePage />}
        <Element name="about">
          <About />
        </Element>
        <Element name="contact">
          <Contact />
        </Element>

        {/* Modal Popup - Giriş Yap */}
        <Modal isOpen={isLoginOpen} onRequestClose={closeLoginModal}>
          <button onClick={closeLoginModal}>Kapat</button>
          <LoginPage goToRegister={openRegisterModal} />
        </Modal>

        {/* Modal Popup - Kayıt Ol */}
        <Modal isOpen={isRegisterOpen} onRequestClose={closeRegisterModal}>
          <button onClick={closeRegisterModal}>Kapat</button>
          <RegisterPage goToLogin={openLoginModal} />
        </Modal>
      </div>

      {/* Footer Bölümü */}
      <footer className="footer bg-dark">
        <p className="text-center text-white">&copy; 2025 CRM Support. Tüm hakları saklıdır.</p>
      </footer>
    </div>
  );
};

export default App;
