import React, { useState, useEffect } from 'react';
import './UserPanel.css';

const UserPanel = () => {
  const [user, setUser] = useState(null);
  const [phone, setPhone] = useState('');
  const [password, setPassword] = useState('');
  const [newRequest, setNewRequest] = useState('');
  const [requests, setRequests] = useState([]);
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Kullanıcı giriş durumu

  // Mock kullanıcı bilgileri
  const mockUser = {
    name: 'Ahmet',
    surname: 'Yılmaz',
    email: 'ahmet.yilmaz@mail.com',
    phone: '1234567890',
    licenses: ['Software Engineer'],
  };

  const mockRequests = [
    { id: 1, text: 'Sistemim çok yavaş çalışıyor', reviewed: false },
    { id: 2, text: 'Yedekleme hatası alıyorum', reviewed: true },
  ];

  // Giriş yapma işlevi
  const handleLogin = (email, password) => {
    // Burada backend'e bağlanarak kontrol yapılabilir.
    if (email === 'ahmet.yilmaz@mail.com' && password === 'password123') {
      setUser(mockUser);
      setRequests(mockRequests);
      setIsLoggedIn(true);
    } else {
      alert('Geçersiz email veya şifre!');
    }
  };

  // Kullanıcı girişi yapılmışsa kullanıcı verilerini göster
  if (!isLoggedIn) {
    return (
      <div className="login-container">
        <h2>Giriş Yap</h2>
        <div>
          <label>Email:</label>
          <input
            type="email"
            onChange={e => setPhone(e.target.value)}
          />
        </div>
        <div>
          <label>Şifre:</label>
          <input
            type="password"
            onChange={e => setPassword(e.target.value)}
          />
        </div>
        <button onClick={() => handleLogin(phone, password)}>Giriş Yap</button>
      </div>
    );
  }

  // Kullanıcı paneli gösterimi
  return (
    <div className="user-panel-container">
      <h2 className="welcome-message">Hoş geldin, {user.name}</h2>

      <div className="user-panel-sections">
        {/* Sol Panel */}
        <div className="panel-section left-panel">
          <h4>Profil Bilgileri</h4>
          <p><strong>Ad:</strong> {user.name}</p>
          <p><strong>Soyad:</strong> {user.surname}</p>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Lisanslar:</strong> {user.licenses.join(', ')}</p>

          <div className="input-group">
            <label>Telefon:</label>
            <input value={phone} onChange={e => setPhone(e.target.value)} />
          </div>
          <div className="input-group">
            <label>Şifre:</label>
            <input type="password" value={password} onChange={e => setPassword(e.target.value)} />
          </div>
          <button className="update-btn">Güncelle</button>
        </div>

        {/* Orta Panel */}
        <div className="panel-section center-panel">
          <h4>Destek Talebi Oluştur</h4>
          <textarea
            value={newRequest}
            onChange={e => setNewRequest(e.target.value)}
            placeholder="Destek talebinizi buraya yazın..."
          />
          <button className="request-btn">Gönder</button>
        </div>

        {/* Sağ Panel */}
        <div className="panel-section right-panel">
          <h4>Geçmiş Talepler</h4>
          {requests.length === 0 ? (
            <p>Henüz talep yok.</p>
          ) : (
            <ul>
              {requests.map((req) => (
                <li key={req.id}>
                  {req.text}
                  <br />
                  <small>Durum: {req.reviewed ? 'Değerlendirildi' : 'Beklemede'}</small>
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>
    </div>
  );
};

export default UserPanel;
