import React from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import LoginPage from "./LoginPage";
import RegisterPage from "./RegisterPage";

const HomePage = () => {
  return (
    <div>
      <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <div className="container">
          <Link className="navbar-brand" to="/">CRM System</Link>
          <div className="d-flex">
            <Link className="btn btn-outline-primary me-2" to="/login">Giriş Yap</Link>
            <Link className="btn btn-primary" to="/register">Kayıt Ol</Link>
          </div>
        </div>
      </nav>

      <header className="text-center p-5 bg-primary text-white">
        <h1>Müşteri İlişkilerinizi Kolayca Yönetin</h1>
        <p>CRM sistemimizle müşteri takibi ve destek yönetimi artık çok kolay!</p>
        <Link className="btn btn-light" to="/register">Hemen Başlayın</Link>
      </header>

      <div className="container my-5">
        <h2 className="text-center mb-4">Öne Çıkan Özellikler</h2>
        <div className="row">
          <div className="col-md-4">
            <h4>Müşteri Yönetimi</h4>
            <p>Müşterilerinizi tek bir panelden yönetin.</p>
          </div>
          <div className="col-md-4">
            <h4>Raporlama</h4>
            <p>Kapsamlı analiz ve raporlama özellikleri.</p>
          </div>
          <div className="col-md-4">
            <h4>Destek Yönetimi</h4>
            <p>Müşteri taleplerini kolayca yönetin.</p>
          </div>
        </div>
      </div>

      <footer className="text-center p-3 bg-light">
        <p>© 2024 CRM System. Tüm Hakları Saklıdır.</p>
      </footer>
    </div>
  );
};

const App = () => {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/login" element={<LoginPage />} />
        <Route path="/register" element={<RegisterPage />} />
      </Routes>
    </Router>
  );
};

export default App;
