import './App.css';
import React, { useState } from 'react';
import RegisterPage from './RegisterPage'; // Kayıt Sayfası
import LoginPage from './LoginPage'; // Giriş Sayfası

const App = () => {
  const [currentPage, setCurrentPage] = useState('home'); // Başlangıçta ana sayfa görünsün

  // Sayfalar arasında geçiş yapmak için fonksiyonlar
  const goToHome = () => setCurrentPage('home');
  const goToAbout = () => setCurrentPage('about');
  const goToContact = () => setCurrentPage('contact');
  const goToLogin = () => setCurrentPage('login');
  const goToRegister = () => setCurrentPage('register');

  return (
    <div className="page-container">
      {/* Navbar */}
      <nav className="navbar">
        <div className="navbar-brand">CRM Support</div>
        <div className="nav-links">
          <a href="#home" onClick={goToHome}>Ana Sayfa</a>
          <a href="#about" onClick={goToAbout}>Hakkımızda</a>
          <a href="#contact" onClick={goToContact}>İletişim</a>
          <a href="#" onClick={goToLogin}>Giriş Yap</a>
          <a href="#" onClick={goToRegister}>Kayıt Ol</a>
        </div>
      </nav>

      {/* İçerik Bölümü */}
      <div className="content">
        {currentPage === 'home' && (
          <div className="home-section">
            <div className="box">
              <h2>CRM Support Sistemi</h2>
              <p>IT firmalarına lisans sattığınız müşterilerle kolay destek yönetimi</p>
            </div>
            <div className="box">
              <h2>Hakkımızda</h2>
              <p>
                CRM Support, Creati gibi lisans sağlayan firmaların müşterileriyle etkili destek süreçleri yürütmesi için tasarlanmış bir platformdur.
              </p>
            </div>
            <div className="box">
              <h2>İletişim</h2>
              <p>Bizimle iletişime geçmek için: destek@crmsupport.com</p>
              <p>Adres: Teknopark İstanbul, Bina 2, No: 301</p>
            </div>
          </div>
        )}

        {currentPage === 'about' && (
          <div className="box">
            <h2>Hakkımızda</h2>
            <p>Burada hakkımızda bölümü olacak.</p>
          </div>
        )}

        {currentPage === 'contact' && (
          <div className="box">
            <h2>İletişim</h2>
            <p>Burada iletişim bölümü olacak.</p>
          </div>
        )}

        {currentPage === 'login' && <LoginPage goToRegister={goToRegister} />}
        {currentPage === 'register' && <RegisterPage goToLogin={goToLogin} />}
      </div>
    </div>
  );
}

export default App;
