import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Link } from 'react-scroll';
import HomePage from './HomePage'; // Örnek bileşenler
import About from './About';
import Contact from './Contact';
import UserPanel from './UserPanel';

const App = ({ user, handleLoginSuccess, handleLogout }) => {
  const navigate = useNavigate();

  // Kullanıcıyı giriş sayfasına yönlendiren fonksiyon
  const goToLogin = () => {
    navigate('/login');
  };

  // Kullanıcıyı kayıt sayfasına yönlendiren fonksiyon
  const goToRegister = () => {
    navigate('/register');
  };

  useEffect(() => {
    // Eğer kullanıcı varsa, otomatik olarak UserPanel sayfasına yönlendir.
    if (user) {
      navigate('/userpanel'); // 'userpanel' rotasına yönlendiriyoruz.
    }
  }, [user, navigate]);

  return (
    <div className="page-container">
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
        <a className="navbar-brand mx-auto" href="#home">CRM Sistemi</a>
        <div className="collapse navbar-collapse">
          <ul className="navbar-nav ml-auto mx-auto">
            <li className="nav-item">
              <Link className="nav-link" to="home" smooth={true} duration={500}>Ana Sayfa</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="about" smooth={true} duration={500}>Hakkımızda</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="contact" smooth={true} duration={500}>İletişim</Link>
            </li>
            {!user ? (
              <>
                <li className="nav-item">
                  <a className="nav-link" href="#" onClick={goToLogin}>Giriş Yap</a>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="#" onClick={goToRegister}>Kayıt Ol</a>
                </li>
              </>
            ) : (
              <li className="nav-item">
                <a className="nav-link" href="#" onClick={handleLogout}>Çıkış Yap</a>
              </li>
            )}
          </ul>
        </div>
      </nav>

      <div className="content">
        <HomePage />
        <About />
        <Contact />
        {user && <UserPanel user={user} />} {/* Sadece kullanıcı varsa UserPanel'ı render et */}
      </div>

      <footer className="footer bg-dark">
        <p className="text-center text-white">&copy; 2025 CRM Support. Tüm hakları saklıdır.</p>
      </footer>
    </div>
  );
};

export default App;
