import React from 'react';
import { useNavigate } from 'react-router-dom'; // navigate kullanacağız

const UserPanel = ({ user }) => {
  const navigate = useNavigate(); // useNavigate hook'u

  // Eğer kullanıcı yoksa, login sayfasına yönlendiriyoruz
  if (!user) {
    navigate('/login');
    return null; // Yönlendirme yapılacağı için başka içerik render etmiyoruz
  }

  return (
    <div className="user-panel-container">
      <h1>Hoşgeldiniz, {user.name} {user.surname}!</h1>
      <p>Email: {user.email}</p>
      <button onClick={() => navigate('/home')}>Ana Sayfaya Git</button>
    </div>
  );
};

export default UserPanel;
