import './App.css';
import React, { useState } from 'react';
import LoginPage from './LoginPage';
import RegisterPage from './RegisterPage';
import 'bootstrap/dist/css/bootstrap.min.css'; // Bootstrap CSS import edilmelidir

const App = () => {
  const [currentPage, setCurrentPage] = useState('home'); // Başlangıçta ana sayfa görünsün
  const [showRegisterModal, setShowRegisterModal] = useState(false); // Kayıt ol modal'ı için state

  // Kayıt sayfasına geçiş
  const goToRegister = () => {
    setShowRegisterModal(true); // Modalı açıyoruz
  };

  // Giriş sayfasına geçiş
  const goToLogin = () => {
    setCurrentPage('login'); // Giriş sayfasına geçiş
  };

  // Ana sayfaya geçiş
  const goToHome = () => {
    setCurrentPage('home'); // Ana sayfa
  };

  // Hakkımızda sayfasına geçiş
  const goToAbout = () => {
    setCurrentPage('about'); // Hakkımızda sayfası
  };

  // İletişim sayfasına geçiş
  const goToContact = () => {
    setCurrentPage('contact'); // İletişim sayfası
  };

  // Kayıt ol modal'ını kapat
  const closeRegisterModal = () => {
    setShowRegisterModal(false); // Modal'ı kapatıyoruz
  };

  return (
    <div className="page-container">
      {/* Navbar bölümü */}
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
        <a className="navbar-brand mx-auto" href="#home">CRM Sistemi</a>
        <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav ml-auto mx-auto">
            <li className="nav-item">
              <a className="nav-link" href="#home" onClick={goToHome}>Ana Sayfa</a>
            </li>
            <li className="nav-item">
              <a className="nav-link" href="#about" onClick={goToAbout}>Hakkımızda</a>
            </li>
            <li className="nav-item">
              <a className="nav-link" href="#contact" onClick={goToContact}>İletişim</a>
            </li>
            <li className="nav-item">
              <a className="nav-link" href="#" onClick={goToLogin}>Giriş Yap</a>
            </li>
            <li className="nav-item">
              <a className="nav-link" href="#" onClick={goToRegister}>Kayıt Ol</a>
            </li>
          </ul>
        </div>
      </nav>

      {/* Sayfa İçeriği */}
      <div className="content">
        <section id="home" style={{ display: currentPage === 'home' ? 'block' : 'none' }}>
          <h2>Ana Sayfa</h2>
          <p>Burada ana sayfa içeriği olacak.</p>
        </section>

        <section id="about" style={{ display: currentPage === 'about' ? 'block' : 'none' }}>
          <h2>Hakkımızda</h2>
          <p>Burada hakkımızda bölümü olacak.</p>
        </section>

        <section id="contact" style={{ display: currentPage === 'contact' ? 'block' : 'none' }}>
          <h2>İletişim</h2>
          <p>Burada iletişim bölümü olacak.</p>
        </section>

        {currentPage === 'login' && <LoginPage goToRegister={goToRegister} />}
        {currentPage === 'register' && <RegisterPage goToLogin={goToLogin} />}
        
        {/* Kayıt Ol Modal */}
        {showRegisterModal && (
          <div className="modal" onClick={closeRegisterModal}>
            <div className="modal-content" onClick={(e) => e.stopPropagation()}>
              <RegisterPage goToLogin={goToLogin} />
              <button onClick={closeRegisterModal} className="close-btn">X</button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
