// UserPanel.js
import React, { useState, useEffect } from 'react';
import './UserPanel.css';
import { Link } from 'react-scroll';

const UserPanel = () => {
  const [user, setUser] = useState(null);
  const [phone, setPhone] = useState('');
  const [password, setPassword] = useState('');
  const [requests, setRequests] = useState([]);
  const [newRequest, setNewRequest] = useState('');

  useEffect(() => {
    const mockUser = {
      name: 'Ayşe',
      surname: 'Yılmaz',
      email: 'ayse@example.com',
      phone: '05555555555',
      licenses: ['Temel Lisans', 'Pro Lisans']
    };
    const mockRequests = [
      { id: 1, text: 'Şifre sıfırlama talebi', reviewed: true },
      { id: 2, text: 'Lisans anahtarım çalışmıyor', reviewed: false }
    ];

    setUser(mockUser);
    setPhone(mockUser.phone);
    setRequests(mockRequests);
  }, []);

  return (
    <div className="user-panel-container">
      {/* Navbar buraya eklenebilir */}
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
        <a className="navbar-brand mx-auto" href="#home">CRM Sistemi</a>
        <div className="collapse navbar-collapse">
          <ul className="navbar-nav ml-auto mx-auto">
            <li className="nav-item">
              <Link className="nav-link" to="home" smooth={true} duration={500}>Ana Sayfa</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="about" smooth={true} duration={500}>Hakkımızda</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="contact" smooth={true} duration={500}>İletişim</Link>
            </li>
            <li className="nav-item">
              <a className="nav-link" href="#">Giriş Yap</a>
            </li>
            <li className="nav-item">
              <a className="nav-link" href="#">Kayıt Ol</a>
            </li>
          </ul>
        </div>
      </nav>

      {/* Kullanıcı paneli içeriği */}
      <h2>Hoş Geldiniz, {user?.name}</h2>
      <div className="panel-content">
        {/* Panel içeriği buraya gelecek */}
      </div>
    </div>
  );
};

export default UserPanel;
