import React, { useState, useEffect } from 'react';
import './UserPanel.css';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const UserPanel = () => {
  const [user, setUser] = useState(null);
  const [phone, setPhone] = useState('');
  const [password, setPassword] = useState('');
  const [newRequest, setNewRequest] = useState('');
  const [requests, setRequests] = useState([]);
  const [loading, setLoading] = useState(true); // Yükleme durumu kontrolü ekledik
  
  const navigate = useNavigate();

  // Kullanıcı ve taleplerin alındığı useEffect
  useEffect(() => {
    axios.get('/api/user/profile')
      .then(res => {
        setUser(res.data);
        setPhone(res.data.phone);
        setLoading(false); // Yükleme bitti
      })
      .catch(err => {
        console.error('Kullanıcı bilgileri alınamadı:', err);
        setLoading(false); // Yükleme bitti, fakat hata aldı
        navigate('/login'); // Hata olduğunda login sayfasına yönlendir
      });

    axios.get('/api/user/requests')
      .then(res => setRequests(res.data))
      .catch(err => console.error('Talepler alınamadı:', err));
  }, [navigate]);

  const handleUpdateInfo = () => {
    axios.put('/api/user/update', {
      phone,
      password,
    }).then(() => {
      alert('Bilgiler güncellendi.');
      navigate('/'); // Ana sayfaya yönlendir
    }).catch(err => {
      console.error('Güncelleme hatası:', err);
      alert('Güncelleme başarısız.');
    });
  };

  const handleRequestSubmit = () => {
    if (!newRequest.trim()) return;

    axios.post('/api/user/requests', {
      text: newRequest,
    }).then(res => {
      setRequests(prev => [...prev, res.data]);
      setNewRequest('');
    }).catch(err => {
      console.error('Talep gönderilemedi:', err);
    });
  };

  const handleReview = (id) => {
    axios.put(`/api/user/requests/${id}/review`)
      .then(() => {
        setRequests(prev =>
          prev.map(req =>
            req.id === id ? { ...req, reviewed: true } : req
          )
        );
      }).catch(err => {
        console.error('Talep değerlendirme hatası:', err);
      });
  };

  // Yükleniyor durumunu kontrol et
  if (loading) return <div>Yükleniyor...</div>;

  if (!user) return <div>Kullanıcı bulunamadı. Giriş yapmanız gerekebilir.</div>;

  return (
    <div className="user-panel-container">
      <h2 className="welcome-message">Hoş geldin, {user.name}</h2>

      <div className="user-panel-sections">
        {/* Sol Panel */}
        <div className="panel-section left-panel">
          <h4>Profil Bilgileri</h4>
          <p><strong>Ad:</strong> {user.name}</p>
          <p><strong>Soyad:</strong> {user.surname}</p>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Lisanslar:</strong> {user.licenses.join(', ')}</p>

          <div className="input-group">
            <label>Telefon:</label>
            <input value={phone} onChange={e => setPhone(e.target.value)} />
          </div>
          <div className="input-group">
            <label>Şifre:</label>
            <input type="password" value={password} onChange={e => setPassword(e.target.value)} />
          </div>
          <button className="update-btn" onClick={handleUpdateInfo}>Güncelle</button>
        </div>

        {/* Orta Panel */}
        <div className="panel-section center-panel">
          <h4>Destek Talebi Oluştur</h4>
          <textarea
            value={newRequest}
            onChange={e => setNewRequest(e.target.value)}
            placeholder="Destek talebinizi buraya yazın..."
          />
          <button className="request-btn" onClick={handleRequestSubmit}>Gönder</button>
        </div>

        {/* Sağ Panel */}
        <div className="panel-section right-panel">
          <h4>Geçmiş Talepler</h4>
          {requests.length === 0 ? (
            <p>Henüz talep yok.</p>
          ) : (
            <ul>
              {requests.map((req) => (
                <li key={req.id}>
                  {req.text}
                  <br />
                  <small>Durum: {req.reviewed ? 'Değerlendirildi' : 'Beklemede'}</small>
                  {!req.reviewed && (
                    <button onClick={() => handleReview(req.id)}>Değerlendir</button>
                  )}
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>
    </div>
  );
};

export default UserPanel;
