import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useNavigate } from 'react-router-dom';
import HomePage from './HomePage';
import About from './About';
import Contact from './Contact';
import LoginPage from './LoginPage';
import RegisterPage from './RegisterPage';
import UserPanel from './UserPanel';

function App() {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  // Login success handler
  const handleLoginSuccess = (userData) => {
    setUser(userData);
    navigate('/userpanel'); // Kullanıcıyı UserPanel sayfasına yönlendir
  };

  return (
    <Router>
      <div className="page-container">
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
          <a className="navbar-brand mx-auto" href="#home">CRM Sistemi</a>
          <div className="collapse navbar-collapse">
            <ul className="navbar-nav ml-auto mx-auto">
              <li className="nav-item">
                <Link className="nav-link" to="/home">Ana Sayfa</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/about">Hakkımızda</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/contact">İletişim</Link>
              </li>
              {!user ? (
                <>
                  <li className="nav-item">
                    <a className="nav-link" href="#" onClick={() => navigate('/login')}>Giriş Yap</a>
                  </li>
                  <li className="nav-item">
                    <a className="nav-link" href="#" onClick={() => navigate('/register')}>Kayıt Ol</a>
                  </li>
                </>
              ) : (
                <li className="nav-item">
                  <a className="nav-link" href="#" onClick={() => { setUser(null); navigate('/'); }}>Çıkış Yap</a>
                </li>
              )}
            </ul>
          </div>
        </nav>

        <Routes>
          <Route path="/home" element={<HomePage />} />
          <Route path="/about" element={<About />} />
          <Route path="/contact" element={<Contact />} />
          <Route path="/login" element={<LoginPage setUser={handleLoginSuccess} />} />
          <Route path="/register" element={<RegisterPage />} />
          <Route path="/userpanel" element={user ? <UserPanel user={user} /> : <HomePage />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
