import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/LoginPage.css';

const LoginPage = ({ setUser }) => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [isRegister, setIsRegister] = useState(false); // Form geçişi için state

  const navigate = useNavigate(); // useNavigate kullanarak yönlendirme işlemi

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (isRegister) {
      // Kayıt işlemleri
      alert('Kayıt başarılı!');
      setIsRegister(false); // Kayıt sonrası giriş formuna dönüyoruz
    } else {
      // Giriş işlemleri
      const userData = {
        name: 'Kullanıcı Adı',
        surname: 'Kullanıcı Soyadı',
        email: formData.email
      };

      setUser(userData);
      navigate('/UserPanel'); // Kullanıcı giriş yaptıktan sonra yönlendirme
    }
  };

  return (
    <div className="login-page-container">
      <div className="login-form">
        <h3 className="text-center mb-4">{isRegister ? 'Kayıt Ol' : 'Giriş Yap'}</h3>
        
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label htmlFor="email" className="form-label">Email Adresi</label>
            <input
              type="email"
              className="form-control"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
              placeholder="Email adresinizi girin"
            />
          </div>
          <div className="mb-3">
            <label htmlFor="password" className="form-label">Şifre</label>
            <input
              type="password"
              className="form-control"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
              placeholder="Şifrenizi girin"
            />
          </div>
          <button type="submit" className="btn w-100">{isRegister ? 'Kayıt Ol' : 'Giriş Yap'}</button>
        </form>

        {/* Kayıt Ol kısmı */}
        <p className="text-center mt-3">
          {isRegister ? 'Hesabınız var mı? ' : 'Hesabınız yok mu? '}
          <a href="#" onClick={(e) => {
            e.preventDefault();
            setIsRegister(!isRegister); // Formları değiştir
          }}>
            {isRegister ? 'Giriş yap' : 'Kayıt ol'}
          </a>
        </p>
      </div>
    </div>
  );
};

export default LoginPage;
