// src/pages/RegisterPage.js

import React, { useState } from 'react';
import '../styles/LoginPage.css'; // Aynı stil kullanılabilir

const RegisterPage = ({ goToLogin }) => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    confirmPassword: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Basit doğrulama örneği
    if (formData.password !== formData.confirmPassword) {
      alert("Şifreler uyuşmuyor!");
      return;
    }

    alert('Kayıt başarılı!');
    goToLogin(); // Kayıt sonrası login ekranına geçiş
  };

  return (
    <div className="login-page-container">
      <div className="login-form">
        <h3 className="text-center mb-4">Kayıt Ol</h3>
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label htmlFor="email" className="form-label">Email Adresi</label>
            <input
              type="email"
              className="form-control"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
              placeholder="Email adresinizi girin"
            />
          </div>
          <div className="mb-3">
            <label htmlFor="password" className="form-label">Şifre</label>
            <input
              type="password"
              className="form-control"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
              placeholder="Şifrenizi girin"
            />
          </div>
          <div className="mb-3">
            <label htmlFor="confirmPassword" className="form-label">Şifre Tekrar</label>
            <input
              type="password"
              className="form-control"
              id="confirmPassword"
              name="confirmPassword"
              value={formData.confirmPassword}
              onChange={handleChange}
              required
              placeholder="Şifrenizi tekrar girin"
            />
          </div>
          <button type="submit" className="btn w-100">Kayıt Ol</button>
        </form>

        <p className="text-center mt-3">
          Zaten hesabınız var mı?{" "}
          <a href="#" onClick={(e) => {
            e.preventDefault();
            goToLogin(); // Bu artık hata vermez
          }}>
            Giriş yap
          </a>
        </p>
      </div>
    </div>
  );
};

export default RegisterPage;
