// src/pages/RegisterPage.js

import React, { useState } from 'react';
import '../styles/LoginPage.css';

const RegisterPage = ({ goToLogin }) => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    confirmPassword: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert('KayÄ±t baÅŸarÄ±lÄ±');
    goToLogin(); // ðŸ”¥ Burada da hata oluyordu
  };

  return (
    <div className="login-page-container">
      <div className="login-form">
        <h3 className="text-center mb-4">KayÄ±t Ol</h3>
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label htmlFor="email" className="form-label">Email</label>
            <input
              type="email"
              className="form-control"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
          <div className="mb-3">
            <label htmlFor="password" className="form-label">Åžifre</label>
            <input
              type="password"
              className="form-control"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>
          <div className="mb-3">
            <label htmlFor="confirmPassword" className="form-label">Åžifre Tekrar</label>
            <input
              type="password"
              className="form-control"
              id="confirmPassword"
              name="confirmPassword"
              value={formData.confirmPassword}
              onChange={handleChange}
              required
            />
          </div>
          <button type="submit" className="btn w-100">KayÄ±t Ol</button>
        </form>

        <p className="text-center mt-3">
          HesabÄ±nÄ±z var mÄ±?{' '}
          <a href="#" onClick={(e) => {
            e.preventDefault();
            goToLogin();
          }}>
            GiriÅŸ yap
          </a>
        </p>
      </div>
    </div>
  );
};

export default RegisterPage;
