import React from 'react';
import '../styles/Navbar.css'; // Navbar stil dosyası

const Navbar = () => {
  const scrollToSection = (id) => {
    console.log(`Attempting to scroll to ${id}`);
    const section = document.getElementById(id);
    console.log(section); // Konsolda elementi kontrol et
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
    } else {
      console.error(`Element with id "${id}" not found.`);
    }
  };

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <h1>CRM Support</h1>
      </div>
      <div className="navbar-right">
        <ul>
          <li><a onClick={() => scrollToSection('main')}>Ana Sayfa</a></li> {/* Ana sayfaya kaydır */}
          <li><a onClick={() => scrollToSection('about')}>Hakkımızda</a></li> {/* Hakkımızda kısmına kaydır */}
          <li><a onClick={() => scrollToSection('contact')}>İletişim</a></li> {/* İletişim kısmına kaydır */}
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
