import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import HomePage from './pages/HomePage';
import About from './sections/About';
import Contact from './sections/Contact';
import Navbar from './components/Navbar';
import Footer from './components/Footer';

const App = () => {
  return (
    <Router>
      <Navbar /> {/* Navbar her sayfada gösterilecek */}
      <Routes>
        <Route path="/" element={<HomePage />} /> {/* Ana Sayfa */}
        <Route path="/about" element={<About />} /> {/* Hakkımızda Sayfası */}
        <Route path="/contact" element={<Contact />} /> {/* İletişim Sayfası */}
      </Routes>
      <Footer /> {/* Footer her sayfada gösterilecek */}
    </Router>
  );
};

export default App;
