import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import HomePage from './pages/HomePage';
import Main from './sections/Main';  // Main sayfası
import About from './sections/About';  // About sayfası
import Contact from './sections/Contact';  // Contact sayfası
import LoginPage from './pages/LoginPage';  // LoginPage
import RegisterPage from './pages/RegisterPage';  // RegisterPage

const App = () => {
  const [isLoginOpen, setIsLoginOpen] = useState(false);
  const [isRegisterOpen, setIsRegisterOpen] = useState(false);

  const goToLogin = () => {
    setIsLoginOpen(true);
    setIsRegisterOpen(false);
  };

  const goToRegister = () => {
    setIsRegisterOpen(true);
    setIsLoginOpen(false);
  };

  const closeForms = () => {
    setIsLoginOpen(false);
    setIsRegisterOpen(false);
  };

  return (
    <Router>
      <div>
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
          <a className="navbar-brand" href="/">CRM Sistemi</a>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav ml-auto">
              <li className="nav-item">
                <a className="nav-link" href="/">Ana Sayfa</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="/about">Hakkımızda</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="/contact">İletişim</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="#" onClick={goToLogin}>Giriş Yap</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="#" onClick={goToRegister}>Kayıt Ol</a>
              </li>
            </ul>
          </div>
        </nav>

        <Routes>
          {/* Sayfalar */}
          <Route path="/" element={<HomePage />} />
          <Route path="/main" element={<Main />} />
          <Route path="/about" element={<About />} />
          <Route path="/contact" element={<Contact />} />
        </Routes>

        {/* Login ve Register Formları sadece pop-up olarak görünecek */}
        {isLoginOpen && (
          <div className="form-popup-box">
            <button className="close-btn" onClick={closeForms}>X</button>
            <LoginPage goToRegister={goToRegister} />
          </div>
        )}

        {isRegisterOpen && (
          <div className="form-popup-box">
            <button className="close-btn" onClick={closeForms}>X</button>
            <RegisterPage goToLogin={goToLogin} />
          </div>
        )}
      </div>
    </Router>
  );
};

export default App;
