import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/LoginPage.css';

const LoginPage = ({ setUser, goToRegister }) => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Demo amaçlı giriş başarılı kabul ediliyor
    const userData = {
      name: 'Kullanıcı Adı',
      surname: 'Kullanıcı Soyadı',
      email: formData.email
    };

    setUser(userData);
    navigate('/main');
  };

  return (
    <div className="login-page-container">
      <div className="login-form">
        <h3 className="text-center mb-4">Giriş Yap</h3>
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label htmlFor="email">Email</label>
            <input type="email" name="email" value={formData.email} onChange={handleChange} required />
          </div>
          <div className="mb-3">
            <label htmlFor="password">Şifre</label>
            <input type="password" name="password" value={formData.password} onChange={handleChange} required />
          </div>
          <button type="submit" className="login-btn">Giriş Yap</button>
        </form>
        <p style={{ textAlign: 'center', marginTop: '12px' }}>
          Hesabınız yok mu?{' '}
          <a href="#" onClick={(e) => {
            e.preventDefault();
            if (typeof goToRegister === 'function') goToRegister();
          }}>
            Kayıt ol
          </a>
        </p>
      </div>
    </div>
  );
};

export default LoginPage;
