// src/pages/LoginPage.js

import React, { useState } from 'react';
import '../styles/LoginPage.css';

const LoginPage = ({ goToRegister }) => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert('GiriÅŸ baÅŸarÄ±lÄ±');
  };

  return (
    <div className="login-page-container">
      <div className="login-form">
        <h3 className="text-center mb-4">GiriÅŸ Yap</h3>
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label htmlFor="email" className="form-label">Email Adresi</label>
            <input
              type="email"
              className="form-control"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
          <div className="mb-3">
            <label htmlFor="password" className="form-label">Åžifre</label>
            <input
              type="password"
              className="form-control"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>
          <button type="submit" className="btn w-100">GiriÅŸ Yap</button>
        </form>

        <p className="text-center mt-3">
          HesabÄ±nÄ±z yok mu?{' '}
          <a href="#" onClick={(e) => {
            e.preventDefault();
            goToRegister(); // ðŸ”¥ Burada hata oluyordu
          }}>
            KayÄ±t ol
          </a>
        </p>
      </div>
    </div>
  );
};

export default LoginPage;
